mapper.select:
SELECT  
	COUNT(ID_CURSO)
FROM 
	TBG_CURSO


		LG_ACTIVO = #{activo}


		DE_TITULO = #{titulo}


		DE_NIVEL = #{nivel}


		NU_HORAS = #{horas}

SELECT
	QUERY2.*
FROM
	(SELECT

			/* FIRST_ROWS(${filtro.pageSize}) */

		ROWNUM RNUM, 
		QUERY1.*
	FROM
		(
			SELECT  
				CURSO.ID_CURSO,
				CURSO.LG_ACTIVO,
				CURSO.DE_TITULO,
				CURSO.DE_NIVEL,
				CURSO.NU_HORAS,
				CURSO.DE_TEMARIO,
				CURSO.ID_TUTOR
			FROM 
				TBG_CURSO CURSO


					LG_ACTIVO = #{activo}


					DE_TITULO = #{titulo}


					DE_NIVEL = #{nivel}


					NU_HORAS = #{horas}



				ORDER BY ${filtro.orderBy} ${filtro.orderSort}

		) QUERY1


			WHERE ROWNUM <= ${filtro.lastResult}


) QUERY2


		RNUM > ${firstResult}

mapper.update:
UPDATE TBG_CURSO SET LG_ACTIVO = #{activo}, DE_TITULO = #{titulo,jdbcType=VARCHAR}, DE_NIVEL = #{nivel,jdbcType=VARCHAR}, NU_HORAS = #{horas,jdbcType=NUMERIC},
DE_TEMARIO = #{temario,jdbcType=VARCHAR},
ID_TUTOR = #{tutor.id,jdbcType=NUMERIC} WHERE ID_CURSO = #{id,jdbcType=NUMERIC}
